        // ctx.drawImage(img, 0, 0);
        // ctx.strokeRect(20, 20, 50, 50);
    }, [])

    return(
        <React.Fragment>
        <canvas ref={imageRef} width='300px' height='300px'>

        </canvas>    
        </React.Fragment>
    )
};

export default SingleImage;
