import { useRef } from 'react';
import classes from './BoxForm.module.css';

const BoxForm = (props) => {
    console.log(props.coordinate)
    const description = useRef();

    const submitHandler = (e) => {
        e.preventDefault();
        console.log('Submit!', description.current.value);
        props.setNewDraw();

        const newImage = {
            coordinate: props.coordinate,
            description: description.current.value,
            url: props.url
        }

        fetch('https://images-4bdb1-default-rtdb.asia-southeast1.firebasedatabase.app/images.json', {
            method: 'POST',
            body: JSON.stringify(newImage),
            headers: {
                'Content-Type': 'application/json'
            }
        }).then(res => res.json).then(rs => console.log(rs));

    };

    return(
        <form 
            className={classes.description} 
            style={{ top: `${props.coordinate.x}`}}
            onSubmit={submitHandler}>
            <input  
                type="text" 
                placeholder="Description" 
                ref={description} 
                required/>
            <button type='submit'>Save</button>
        </form>
    )
};

export default BoxForm;
