import classes from './Canvas.module.css';
import React, { useRef, useEffect, useState } from 'react';
import BoxForm from "./BoxForm";

const Canvas = (props) => {
  console.log('canvas', props.imageUrl);
  const canvas = useRef();
  let currentBox =  null; 
  let image = new Image();
  image.src = props.imageUrl;
  image.width = '700px';

  const [boxes, setBoxes] = useState([]);
  const [annotation, setAnnotation] = useState(null);
  const [newDraw, setNewDraw] = useState(false);
  const [coordinate, setCoordinate] = useState({x: 0, y: 0, w: 0, h: 0});

  const drawBox = (ctx, box) => {
    const { x, y, w, h } = box.coor;
    ctx.beginPath();
    ctx.strokeStyle = 'blue';
    ctx.lineWidth = 1;
    ctx.rect(x, y, w, h);
    ctx.stroke();
    ctx.fillText(box.desc, x, y - 5);
  }

  const drawBoxes = () => {
    const canvasEle = canvas.current;
    canvasEle.width = canvasEle.clientWidth;
    canvasEle.height = canvasEle.clientHeight;
    let ctx = canvasEle.getContext("2d");

    if (ctx !== null) {
      ctx.clearRect(0, 0, canvas.current.clientWidth, canvas.current.clientHeight);  
      ctx.drawImage(image, 0, 0);
      boxes.map(box => drawBox(ctx, box));
      if (currentBox !== null) {
        drawBox(ctx, currentBox);
      }     
    }
  }

  const handleMouseDown = e => {
    if (currentBox === null) {
      const x = parseInt(e.nativeEvent.offsetX - canvas.current.clientLeft);
      const y = parseInt(e.nativeEvent.offsetY - canvas.current.clientTop);
      currentBox = {coor: {x, y, w: 0, h: 0}, desc: ''};  
    }
  }  

  const handleMouseUp = e => {
    if (currentBox !== null && currentBox.w !== 0 && currentBox.h !== 0) {
      setBoxes([...boxes, currentBox]);
      setCoordinate(currentBox.coor);
      setNewDraw(true);
      // drawBoxes();
    } 
  }  

  const handleMouseOut = e => {
    currentBox = null
      drawBoxes();
  }

  const handleMouseMove = e => {
    if (currentBox !== null) {
      const { x, y, w, h } = currentBox.coor;
      const mouseX = parseInt(e.nativeEvent.offsetX - canvas.current.clientLeft);
      const mouseY = parseInt(e.nativeEvent.offsetY - canvas.current.clientTop);
      currentBox.coor = {x, y, w: mouseX - x, h: mouseY - y};
      drawBoxes();
    }
  }

  const annotationHandler = (text) => {
    setBoxes([...boxes, currentBox]);
    currentBox = null;
  };


  // initialize the canvas context
  useEffect(() => {
    // dynamically assign the width and height to canvas
  }, []);

  useEffect(() => {   
    drawBoxes();
  }, [boxes]);

  return (
      <div className={classes.page}>
        <canvas 
          onMouseDown={handleMouseDown} 
          onMouseUp={handleMouseUp}
          onMouseOut={handleMouseOut}
          onMouseMove={handleMouseMove}
          ref={canvas}>    
        </canvas>
        {newDraw && <BoxForm 
          setNewDraw={() => setNewDraw(false)}
          coordinate={coordinate}
          postAnnotation={annotationHandler}
          url={props.imageUrl}
          id={props.imageKey}
          />}
      </div>
      );
}

export default Canvas;
