import React, { useRef, useEffect, useState } from 'react';
import { classicNameResolver } from 'typescript';
import catsImg from '../images/cats.jpg';
import BoxForm from "./BoxForm";
import classes from './Canvas.module.css';


const Canvas = () => {
    const canvas = useRef();
    const box = useRef();
    let ctx = null;
    let currentBox =  null; 
    let boxArray = [{x: 127, y: 55, w: 171, h: 142}];
    let image = new Image();
    image.src = catsImg;

    const [newDraw, setNewDraw] = useState(false);
    const [formPosition, setFormPosition] = useState(0);

    const drawBox = (box) => {
      const borderColor = 'black';
      const borderWidth = 1;
      const { x, y, w, h } = box;
      ctx.beginPath();
      ctx.strokeStyle = borderColor;
      ctx.lineWidth = borderWidth;
      ctx.rect(x, y, w, h);
      ctx.stroke();
      ctx.font = "16px arial";
      ctx.strokeText("Hello World", x, y - 5);
    }
    const drawBoxes = () => {
      if (ctx !== null) {
        ctx.clearRect(0, 0, canvas.current.clientWidth, canvas.current.clientHeight);  
        ctx.drawImage(image, 0, 0);
        boxArray.map(box => drawBox(box));
        if (currentBox !== null) {
          drawBox(currentBox);
        }     
      }
    }

    const handleMouseDown = e => {
      const x = parseInt(e.nativeEvent.offsetX - canvas.current.clientLeft);
      const y = parseInt(e.nativeEvent.offsetY - canvas.current.clientTop);
      currentBox = {x, y, w: 30, h: 30};
      drawBoxes();
    }  

    const handleMouseUp = e => {
      if (currentBox !== null) {
        boxArray.push(currentBox);
        setFormPosition(currentBox.x);
        currentBox = null;
      }
      drawBoxes();
    }  

    const handleMouseOut = e => {
      console.log('Done!')
      setNewDraw(true);
      currentBox = null
      drawBoxes();

    }

    const handleMouseMove = e => {
      if (currentBox !== null) {
        const { x, y, w, h } = currentBox;

        const mouseX = parseInt(e.nativeEvent.offsetX - canvas.current.clientLeft);
        const mouseY = parseInt(e.nativeEvent.offsetY - canvas.current.clientTop);
        currentBox = {x, y, w: mouseX - x, h: mouseY - y};
        drawBoxes();
      }
    }


    // initialize the canvas context
    useEffect(() => {
      // dynamically assign the width and height to canvas
      const canvasEle = canvas.current;
      canvasEle.width = canvasEle.clientWidth;
      canvasEle.height = canvasEle.clientHeight;

      // get context of the canvas
      ctx = canvasEle.getContext("2d");
    }, []);

    useEffect(() => {   
      drawBoxes();
      console.log(boxArray);
    }, []);

    return (
      <div className={classes.page}>
        <canvas
          onMouseDown={handleMouseDown} 
          onMouseUp={handleMouseUp}
          onMouseOut={handleMouseOut}
          onMouseMove={handleMouseMove}
          ref={canvas}> 
        </canvas>
        {newDraw && <BoxForm 
          setNewDraw={() => setNewDraw(false)}
          x={formPosition}/>}
      </div>
    );
  }

  export default Canvas;
