import Canvas from "./components/Canvas";
import classes from './App.module.css';
import { useState } from 'react';
import { storage } from './firebase/firebase';
import { getDownloadURL, uploadBytesResumable, ref } from "firebase/storage";
import { getDatabase, ref as rtref, set } from "firebase/database";

function App() {

  const [image, setImage] = useState('');
  const [imageUrl, setImageUrl] = useState(null);

  const fileChangeHandler = (e) => {
    setImage(e.target.files[0])
  }

  const saveImageIntoDb = (url) => {
    const db = getDatabase();
    set(rtref(db, '/'), {
      url: url
    });
  };

 const fileUploadHandler = () => {

    const storageRef = ref(storage, `/images/${image.name}`);
    const uploadTask = uploadBytesResumable(storageRef, image);
    uploadTask.on('state_changed', (snapshot) => {},
    (err) => {console.log(err)},
    () => {
      getDownloadURL(uploadTask.snapshot.ref)
      .then(url => {
        console.log('app', url);
        setImageUrl(url);
        saveImageIntoDb(url);
      })
    }
 )};


  return(
      <div className={classes.app}>
        <input type="file" onChange={fileChangeHandler} className={classes.chooseFile}/>
        <button type="button" onClick={fileUploadHandler}>Upload Image</button>
        {imageUrl !== null && <Canvas imageUrl={imageUrl}/>}
      </div>
  )
};

export default App;
