import React, { useRef, useEffect } from "react";
import { getDatabase, ref, onValue} from "firebase/database";


const SingleImage = (props) => {


    const imageRef = useRef(null);
    const img = new Image();
    img.src = props.url;
    console.log(img)

    useEffect(() => {
        fetch(`https://images-4bdb1-default-rtdb.asia-southeast1.firebasedatabase.app/images/${props.imgID}/boxes.json`)
        .then(res => res.json())
        .then(data => {
            const canvas = imageRef.current
            const ctx = canvas.getContext('2d')
            img.onload = () => {
                ctx.drawImage(img, 0, 0)
                const boxes = [];
                for (const key in data){
                    boxes.push({
                        coordinate: data[key].coordinate, 
                        description: data[key].description
                    })
                for (const box in boxes) {
                    const coor = box.coordinate.map(num => num*3/5);
                    const {x, y, w, h} = coor
                    ctx.strokeRect(x, y, w, h);
                }
                }
              }
        })
      }, [])

    return(
        <canvas ref={imageRef} width={300} height={300}>
        </canvas>    
    )
};

export default SingleImage;
